import * as types from './actionTypes';

export function receiveParties(json) {
  return { type: types.RECEIVE_PARTIES, parties: json.parties };
}

export function fetchParties() {
    return (dispatch) => {
        fetch('http://localhost:4000/partidos', {
          method: 'GET',
          headers: {
            'Access-Control-Request-Headers': '*'
          }
        })
            .then(response =>
                response.json().then(data => ({
                    data: data,
                    status: response.status
                }))
            )
            .then(response => {
                if(response.status === 200){
                    dispatch(receiveParties(response.data))
                }else{
                    var flash = {
                        type: 'error',
                        title: 'Error getting task list',
                        content: 'There was an error getting the task list. Please try again.'
                    }
                    dispatch({type: "DISPLAY_FLASH", data: flash})
                }
            });
    };
}
