import { connect } from 'react-redux';
import * as partiesActions from '../../actions/partiesActions';
import React from 'react';


class List extends React.Component {

  componentWillMount(){
    this.props.fetchParties()
  }
  renderData = () => {
    if (this.props.parties.length !== 0) {
      return (
        <div style={{display: 'flex', flex: 1, flexDirection: 'column'}}>
        { this.props.parties.parties[0].party_name } - { this.props.parties.parties[0].description }
        { this.props.parties.parties[1].party_name } - { this.props.parties.parties[1].description }
        </div>
      );
    }
  }

  render() {
    return (
      <div className="">
        <div className="">
          { this.renderData() }
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties
  };
}

function mapDispatchToProps(dispatch) {
  return {
    fetchParties: () => {
      dispatch(partiesActions.loadParties())
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(List);
