import initialState from './initialState';
import { SET_VIEW } from '../actions/actionTypes';

export default function views(state = initialState.view, action) {
  switch (action.type) {
    case SET_VIEW:
      return {
        ...state,
        view: action.view
      }

    default:
      return state;
  }
}
