import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Header.css';

const HOME = 'HOME';
const SOBRE = 'SOBRE';
const VOTACOES = 'VOTACOES';

class Header extends Component {
  constructor(props){
    super(props);

    this.state = {
      current: "Home"
    }
  }

  changeElem = (current) => {
    this.setState({
      current: current
    })
  }

  renderPartieRow = () => {
    let data = [];


    if(current === HOME) {
      data.push(<div className="navElems" onClick={() => this.changeElem(HOME)}>Home</div>);
    } else {
      data.push(<div className="navElems" onClick={() => this.changeElem("Home")}>Home</div>);
    }

    if(current === "Sobre") {
      data.push(<div className="navElems" onClick={() => this.changeElem("Sobre")}>Sobre</div>)
    } else {

    }

    if(current === "Votações") {
      data.push(<div className="navElems" onClick={() => this.changeElem("Votações")}>Votações</div>)
    } else {
      data.push(<div className="navElems" onClick={() => this.changeElem("Votações")}>Votações</div>)
    }


    if (this.props.parties.length !== 0) {
      for(let i = 0; i < this.props.parties.length; i++) {

        data.push(
            <div key={i} className="navElems">
                { this.props.parties[i].party_name }
            </div>
        );
      }
    }

    return data;
  }

  render() {
    return (
      <div className="header">
        <div className="title">
          <h1 style={{margin: 0}}>Politicando</h1>
        </div>
        <div className="nav">
          { this.renderPartieRow() }
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties.partieList,
    estado: state
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Header);
