import React, { Component } from 'react';
import { connect } from 'react-redux';
import './LatestNews.css';
import LatestNewsSingleContainer from './LatestNewsSingleContainer';

class LatestNews extends Component {

    renderDoubleNewsRow = () => {
        var data = [];

        for(let i = 0; i < this.props.articles.length; i++){
            for(var j = 0; j < 2; j++) {
                // 
                // if (j === 0 ) {
                //     data.push(<div key={"doubleContainer" + i} className="latestNewsDoubleContainer">)
                // }

                data.push(<LatestNewsSingleContainer key={"newsContainer" + i} title={this.props.article[i].title} content={this.props.article[i].content} />);
                //
                // if(j === 1) {
                //     data.push(</div>)
                // }
            }
        }

        return data;
    }

    render() {
        return (
          <div className="latestNewsMainContainer">
            <h3>Latest Information</h3>

            <div className="latestNewsDoubleContainer">
              <LatestNewsSingleContainer />
              <LatestNewsSingleContainer />
            </div>

            <div className="latestNewsDoubleContainer">
              <LatestNewsSingleContainer />
              <LatestNewsSingleContainer />
            </div>

            <div className="latestNewsDoubleContainer">
              <LatestNewsSingleContainer />
              <LatestNewsSingleContainer />
            </div>

            <div className="latestNewsDoubleContainer">
              <LatestNewsSingleContainer />
              <LatestNewsSingleContainer />
            </div>

          </div>
        );
    }
}

function mapStateToProps(state) {
  return {
      articles: state.articles.all_articles
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(LatestNews);
