      <div className="mainChild">
        <div className="partieInformationContent">
          <h3>{this.props.currentPartie.party_name} : {this.props.currentPartie.description}</h3>
        </div>
        <div className="optionsContainer">
          <Subject style={optionElem} onmouseover={changeStyle} />
          <HowToVote style={optionElem} />
          <EventSeat style={optionElem} />

        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentPartie: state.parties.currentPartie
  };
}

function mapDispatchToProps(dispatch) {
