import React, { Component } from 'react';
import { connect } from 'react-redux';
import './LatestNews.css';
import LatestNewsSingleContainer from './LatestNewsSingleContainer';

class LatestNews extends Component {

    renderDoubleNewsRow() {
        var data = [];

        for(let j = 0; this.props.articles.length / 2; j++){
            data.push(<div className="latestNewsDoubleContainer">);

            for(let i = 0; this.props.articles.length; i++) {
                data.push(<LatestNewsSingleContainer  title={this.props.article[i].title} content={this.props.article[i].content} />)
            }

            data.push(</div>);
        }
    }

  render() {
    return (
      <div className="latestNewsMainContainer">
        <h3>Latest Information</h3>

        <div className="latestNewsDoubleContainer">
          <LatestNewsSingleContainer />
          <LatestNewsSingleContainer />
        </div>

        <div className="latestNewsDoubleContainer">
          <LatestNewsSingleContainer />
          <LatestNewsSingleContainer />
        </div>

        <div className="latestNewsDoubleContainer">
          <LatestNewsSingleContainer />
          <LatestNewsSingleContainer />
        </div>

        <div className="latestNewsDoubleContainer">
          <LatestNewsSingleContainer />
          <LatestNewsSingleContainer />
        </div>

      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
      articles: state.articles.all_articles
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(LatestNews);
