import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Header.css';
import { asyncChangeView } from '../../actions/viewActions';
import { asyncChangeCurrentPartie } from '../../actions/partiesActions';

const HOME = 'HOME';
const SOBRE = 'SOBRE';
const VOTACOES = 'VOTACOES';

class Header extends Component {
  constructor(props){
    super(props);

    this.state = {
      current: HOME,
      open: false,
      index: null
    }
  }

  changeElem = (current) => {
    this.setState({
      current: current
    })
  }

  selectPartie = (i) => {
    this.props.changeView("Partie");
    this.props.changeCurrentPartie(this.props.parties[i]);

    this.setState({
      open: this.state.open ? ((this.state.index === i) ? false : true) : true,
      index: i
    })
  }

  elemFuncs = (currentElem, i = -1) => {
    this.selectPartie(i);
    this.changeElem(currentElem);
  }

  renderPartieRow = () => {
    let data = [];

    if(this.state.current === HOME) {
      data.push(<div className="navElems selectedElem" onClick={() => this.elemFuncs(HOME)}>Home</div>);
    } else {
      data.push(<div className="navElems" onClick={() => this.elemFuncs(HOME)}>Home</div>);
    }

    if(this.state.current  === SOBRE) {
      data.push(<div className="navElems selectedElem" onClick={() => this.elemFuncs(SOBRE)}>Sobre</div>)
    } else {
      data.push(<div className="navElems" onClick={() => this.elemFuncs(SOBRE)}>Sobre</div>)
    }

    if(this.state.current  === VOTACOES) {
      data.push(<div className="navElems selectedElem" onClick={() => this.elemFuncs(VOTACOES)}>Votações</div>)
    } else {
      data.push(<div className="navElems" onClick={() => this.elemFuncs(VOTACOES)}>Votações</div>)
    }

    if (this.props.parties.length !== 0) {
      for(let i = 0; i < this.props.parties.length; i++) {
        if(this.state.current === this.props.parties[i].party_name) {
          data.push(
              <div key={i} className="navElems selectedElem" onClick={() => this.elemFuncs(this.props.parties[i].party_name, i)}>
                  { this.props.parties[i].party_name }
              </div>
          );
        } else {
          data.push(
              <div key={i} className="navElems" onClick={() => this.elemFuncs(this.props.parties[i].party_name, i)}>
                  { this.props.parties[i].party_name }
              </div>
          );
        }
      }
    }

    return data;
  }

  render() {
    return (
      <div className="header">
        <div className="title">
          <h1 style={{margin: 0}}>Politicando</h1>
        </div>
        <div className="nav">
          { this.renderPartieRow() }
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties.partieList,
    estado: state
  };
}

function mapDispatchToProps(dispatch) {
  return {
    changeView: (view) => {
      dispatch(asyncChangeView(view))
    },
    changeCurrentPartie: (partie) => {
      dispatch(asyncChangeCurrentPartie(partie))
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Header);
