import initialState from './initialState';
import { SET_VIEW } from '../actions/actionTypes';

export default function view(state = initialState, action) {
  switch (action.type) {
    case SET_VIEW:
      return Object.assign({}, state, {
        view: action.view
      })

    default:
      return state;
  }
}
