import React, { Component } from 'react';
import './Header.css';

class Header extends Component {

  renderPartieRow = () => {
    let data = [];

    if (this.props.parties.length !== 0) {
      for(let i = 0; i < this.props.parties.length; i++) {

        let title = this.props.parties[i].description + ": " + this.props.parties[i].deputies_republic_chamber + " - Assembleia da República";
        data.push(
            <div key={i} className="partieNameContainer" title={title} onClick={() => this.selectPartie(i)}>
                { this.props.parties[i].party_name } - {this.props.parties[i].deputies_republic_chamber}
            </div>
        );
      }
    }

    return data;
  }

  render() {
    return (
      <div className="header">
        <div className="title">
          <h1 style={{margin: 0}}>Politicando</h1>
        </div>
        <div className="nav">
          <div className="navElems">Home</div>
          <div className="navElems">Sobre</div>
          <div className="navElems">Dúvidas</div>
          <div className="navElems">Partidos</div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Header);
