import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Header.css';

class Header extends Component {
  constructor(props){
    super(props);

    this.state = {
      current: "Home"
    }
  }

  renderPartieRow = () => {
    let data = [];

    if (this.props.parties.length !== 0) {
      for(let i = 0; i < this.props.parties.length; i++) {

        data.push(
            <div key={i} className="navElems">
                { this.props.parties[i].party_name }
            </div>
        );
      }
    }

    return data;
  }

  render() {
    return (
      <div className="header">
        <div className="title">
          <h1 style={{margin: 0}}>Politicando</h1>
        </div>
        <div className="nav">
          <div className="navElems">Home</div>
          <div className="navElems">Sobre</div>
          <div className="navElems">Votações</div>
          { this.renderPartieRow() }
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties.partieList,
    estado: state
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Header);
