import React, { Component } from 'react';
import { connect } from 'react-redux';
import './PartieInformation.css';
import Subject from '@material-ui/icons/Subject';
import HowToVote from '@material-ui/icons/HowToVote';
import EventSeat from '@material-ui/icons/EventSeat'

const optionElem = {
    width: '200px',
    height: '200px',
    margin: '0 50px 0 50px',
    cursor: 'pointer'
}

const optionElemHover = {
    width: '200px',
    height: '200px',
    margin: '0 50px 0 50px',
    cursor: 'pointer',
    backgroundColor: 'black'
}


class PartieInformation extends Component {
  constructor(props) {
    super(props);

    this.state = {
      styled: optionElem
    }
  }

  changeStyle = () => {
    this.setState({
      styled: optionElemHover
    })
    console.log("hey")
  }

  render() {
    return (
      <div className="mainChild">
        <div className="partieInformationContent">
          <h3>{this.props.currentPartie.party_name} : {this.props.currentPartie.description}</h3>
        </div>
        <div className="optionsContainer">
          <Subject style={this.state.styled} onMouseOver={() => this.changeStyle()} />
          <HowToVote style={this.state.styled} onMouseOver={() => this.changeStyle()} />
          <EventSeat style={this.state.styled} onMouseOver={() => this.changeStyle()} />

        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentPartie: state.parties.currentPartie
  };
}

function mapDispatchToProps(dispatch) {
  return { };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(PartieInformation);


