import { connect } from 'react-redux';
import * as partiesActions from '../../actions/partiesActions';
import React from 'react';
import './List.css';


class List extends React.Component {

  componentWillMount(){
    this.props.fetchParties()
  }

  renderData = () => {

    let data = [];

    if (this.props.parties.length !== 0) {
      this.parops.parties.foreach((item, index) => {
        data.push(<div className="partieNameContainer">{ this.props.parties.parties[index].party_name } - { this.props.parties.parties[index].description }</div>);
      })
    }

    return data;
  }

  render() {
    return (
      <div className="mainContainter">
          {this.renderData}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    parties: state.parties
  };
}

function mapDispatchToProps(dispatch) {
  return {
    fetchParties: () => {
      dispatch(partiesActions.loadParties())
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(List);
